import { useEffect } from "react";

pjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pjs.version}/legacy/build/pdf.worker.min.js`;
export default function PDFViewer({pdf}) {

  useEffect(() => {
    if (!pdf)
      return;
    console.log("[DAVID] pjs = ", pjs);
    pjs.getDocument(pdf.url).promise
      .then(function (pdfDoc) {
        console.log("[DAVID] DOC :: ", pdfDoc);
        // const page = pdfDoc.getPage(1);
        // console.log("[DAVID] page :: ", page);
      })
      .catch(e => {
        console.log("[DAVID] Error on loading document! ", e);
      })
      ;
  }, [pdf]);

